    /**
     * Install the RKParkHausModule application.
     *
     * @return boolean True on success, or false
     *
     * @throws RuntimeException Thrown if database tables can not be created or another error occurs
     */
    public function install()
    {
        $logger = $this->container->get('logger');
    
        // Check if upload directories exist and if needed create them
        try {
            $container = $this->container;
            $controllerHelper = new \RK\ParkHausModule\Helper\ControllerHelper($container, $container->get('translator.default'), $container->get('session'), $container->get('logger'));
            $controllerHelper->checkAndCreateAllUploadFolders();
        } catch (\Exception $e) {
            $this->addFlash('error', $e->getMessage());
            $logger->error('{app}: User {user} could not create upload folders during installation. Error details: {errorMessage}.', ['app' => 'RKParkHausModule', 'user' => $userName, 'errorMessage' => $e->getMessage()]);
        
            return false;
        }
        // create all tables from according entity definitions
        try {
            $this->schemaTool->create($this->listEntityClasses());
        } catch (\Exception $e) {
            $this->addFlash('error', $this->__('Doctrine Exception') . ': ' . $e->getMessage());
            $logger->error('{app}: Could not create the database tables during installation. Error details: {errorMessage}.', ['app' => 'RKParkHausModule', 'errorMessage' => $e->getMessage()]);
    
            return false;
        }
    
        // set up all our vars with initial values
        $this->setVar('myDirectory', '');
        $this->setVar('myText', '');
        $this->setVar('vehicleEntriesPerPage', 10);
        $this->setVar('vehicleImageEntriesPerPage', 10);
        $this->setVar('enableShrinkingForVehicleTitleImage', false);
        $this->setVar('shrinkWidthVehicleTitleImage', 800);
        $this->setVar('shrinkHeightVehicleTitleImage', 600);
        $this->setVar('enableShrinkingForVehicleVehicleImage', false);
        $this->setVar('shrinkWidthVehicleVehicleImage', 800);
        $this->setVar('shrinkHeightVehicleVehicleImage', 600);
        $this->setVar('enableShrinkingForVehicleImageVehicleImage', false);
        $this->setVar('shrinkWidthVehicleImageVehicleImage', 800);
        $this->setVar('shrinkHeightVehicleImageVehicleImage', 600);
        $this->setVar('thumbnailModeVehicle',  'inset' );
        $this->setVar('thumbnailWidthVehicleTitleImageView', 32);
        $this->setVar('thumbnailHeightVehicleTitleImageView', 24);
        $this->setVar('thumbnailWidthVehicleTitleImageDisplay', 240);
        $this->setVar('thumbnailHeightVehicleTitleImageDisplay', 180);
        $this->setVar('thumbnailWidthVehicleTitleImageEdit', 240);
        $this->setVar('thumbnailHeightVehicleTitleImageEdit', 180);
        $this->setVar('thumbnailWidthVehicleVehicleImageView', 32);
        $this->setVar('thumbnailHeightVehicleVehicleImageView', 24);
        $this->setVar('thumbnailWidthVehicleVehicleImageDisplay', 240);
        $this->setVar('thumbnailHeightVehicleVehicleImageDisplay', 180);
        $this->setVar('thumbnailWidthVehicleVehicleImageEdit', 240);
        $this->setVar('thumbnailHeightVehicleVehicleImageEdit', 180);
        $this->setVar('thumbnailModeVehicleImage',  'inset' );
        $this->setVar('thumbnailWidthVehicleImageVehicleImageView', 32);
        $this->setVar('thumbnailHeightVehicleImageVehicleImageView', 24);
        $this->setVar('thumbnailWidthVehicleImageVehicleImageDisplay', 240);
        $this->setVar('thumbnailHeightVehicleImageVehicleImageDisplay', 180);
        $this->setVar('thumbnailWidthVehicleImageVehicleImageEdit', 240);
        $this->setVar('thumbnailHeightVehicleImageVehicleImageEdit', 180);
    
        // create the default data
        $this->createDefaultData();
    
        // install subscriber hooks
        $this->hookApi->installSubscriberHooks($this->bundle->getMetaData());
        
    
        // initialisation successful
        return true;
    }
    
