namespace RK\SponsoringModule\Container\Base;

use Zikula\Bundle\HookBundle\AbstractHookContainer as ZikulaHookContainer;
use Zikula\Bundle\HookBundle\Bundle\SubscriberBundle;

/**
 * Base class for hook container methods.
 */
abstract class AbstractHookContainer extends ZikulaHookContainer
{
    /**
     * Define the hook bundles supported by this module.
     *
     * @return void
     */
    protected function setupHookBundles()
    {
        $bundle = new SubscriberBundle('RKSponsoringModule', 'subscriber.rksponsoringmodule..ui_hooks.sponsors', 'ui_hooks', $this->__('rksponsoringmodule. Sponsors Display Hooks'));
        
        // Display hook for view/display templates.
        $bundle->addEvent('display_view', 'rksponsoringmodule.ui_hooks.sponsors.display_view');
        // Display hook for create/edit forms.
        $bundle->addEvent('form_edit', 'rksponsoringmodule.ui_hooks.sponsors.form_edit');
        // Display hook for delete dialogues.
        $bundle->addEvent('form_delete', 'rksponsoringmodule.ui_hooks.sponsors.form_delete');
        // Validate input from an ui create/edit form.
        $bundle->addEvent('validate_edit', 'rksponsoringmodule.ui_hooks.sponsors.validate_edit');
        // Validate input from an ui delete form.
        $bundle->addEvent('validate_delete', 'rksponsoringmodule.ui_hooks.sponsors.validate_delete');
        // Perform the final update actions for a ui create/edit form.
        $bundle->addEvent('process_edit', 'rksponsoringmodule.ui_hooks.sponsors.process_edit');
        // Perform the final delete actions for a ui form.
        $bundle->addEvent('process_delete', 'rksponsoringmodule.ui_hooks.sponsors.process_delete');
        $this->registerHookSubscriberBundle($bundle);
        
        $bundle = new SubscriberBundle('RKSponsoringModule', 'subscriber.rksponsoringmodule..filter_hooks.sponsors', 'filter_hooks', $this->__('rksponsoringmodule. Sponsors Filter Hooks'));
        // A filter applied to the given area.
        $bundle->addEvent('filter', 'rksponsoringmodule.filter_hooks.sponsors.filter');
        $this->registerHookSubscriberBundle($bundle);
        
        
        
    }
}
