namespace RK\WebsiteHelperModule\Event\Base;

use Symfony\Component\EventDispatcher\Event;
use RK\WebsiteHelperModule\Entity\LinkerEntity;

/**
 * Event base class for filtering linker processing.
 */
class AbstractFilterLinkerEvent extends Event
{
    /**
     * @var LinkerEntity Reference to treated entity instance.
     */
    protected $linker;

    public function __construct(LinkerEntity $linker)
    {
        $this->linker = $linker;
    }

    public function getLinker()
    {
        return $this->linker;
    }
}
