        if ($entity instanceof LinkerEntity) {
            $component = 'RKWebsiteHelperModule:Linker:';
            $instance = $entity['id'] . '::';
        
        if ($currentLegacyControllerType == 'admin') {
            if (in_array($currentFunc, ['index', 'view'])) {
                $menu->addChild($this->__('Preview'), [
                    'route' => 'rkwebsitehelpermodule_linker_display',
                    'routeParameters' => ['id' => $entity['id']]
